

package Platera;

import javax.servlet.*;
import javax.servlet.http.*;
import java.io.*;
import java.sql.Clob;
import java.util.Base64;
import javax.servlet.annotation.WebServlet;

@WebServlet(name = "Test", urlPatterns = {"/Test"})
public class Test extends HttpServlet {

    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        
        // For this example, we will assume that the CLOB data is passed as a parameter.
        // Replace this with actual CLOB data as needed.
        String clobData = "";
        
        if (clobData == null || clobData.isEmpty()) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "CLOB data is missing");
            return;
        }
        
        // Set the response content type to image (jpeg for example)
        response.setContentType("image/jpeg");

        // Convert the CLOB data (base64 encoded) to bytes
        byte[] imageBytes = Base64.getDecoder().decode(clobData);

        // Write the image data to the response output stream
        try (OutputStream out = response.getOutputStream()) {
            out.write(imageBytes);
        }
    }
}

